import { useState } from "react";
import axios from "axios";

function App() {

  const [cep, setCep] = useState("");
  const [endereco, setEndereco] = useState(null);

  const buscar = async( ) =>{

    try{
      const response = await axios.get(`https://viacep.com.br/ws/${cep}/json/`);
      setEndereco(response.data);
    }catch(error){
      alert('ERROR 400 Pesquisa não encontrada', error);
      setEndereco(null);  
    }
  };

  return(
<>

          {!endereco &&(

            <div>
              <div className="Card">
                <div className="Title">
                MeuLocal-ViaCep
                </div>
                <div className="Form">
                  <input
                   type="text"
                    name='cep'
                    value={cep} 
                    onChange={(e)=> setCep(e.target.value)} 
                    id='cep' 
                    minLength='8' 
                    maxLength='8'
                    /><br/>
                  <input type="submit" onClick={buscar} value='Descobrir meu Local' className="Botao"/>
                </div>
              </div>
            </div>

          )}






{endereco &&(   
  <div>
    <div className="card">
      <div className="Title">MeuLocal-ViaCep</div> 
      <div className="Title2">Você está em:</div>
      <div className="Items">
      <p>Cidade: {endereco.localidade}</p>
      <p>UF: {endereco.uf}</p>
      <p>Estado: {endereco.estado}</p>
      <p>bairro: {endereco.bairro}</p>
      <p>Ruas: {endereco.logradouro}</p>
      <p>Quadras: {endereco.complemento}</p>
      </div>
    </div>
  </div>        
)}

</>
  );
}

export default App;
